// Importing necessary libraries
import React from 'react';
import { View, Text, Button, TextInput } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';
import SignUpScreen from './SignUpScreen';
import LoginScreen from './LoginScreen';

// Landing Screen
// Styles
const styles = StyleSheet.create({
  container: {
    backgroundColor: 'red', 
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#fff',
  },
  logo: {
    width: 100,
    height: 100,
  },
  welcomeText: {
    fontSize: 24,
    textAlign: 'center',
    marginTop: 20,
  },
  descriptionText: {
    fontSize: 18,
    textAlign: 'center',
    marginTop: 10,
  },
  buttonContainer: {
    width: '80%',
    marginTop: 20,
  },
  button: {
    backgroundColor: '#4CAF50', // Green
    color: '#fff', // White
    paddingVertical: 15,
    paddingHorizontal: 32,
    textAlign: 'center',
    fontSize: 16,
    marginBottom: 10,
  },
});

// Landing Screen
const LandingScreen = ({ navigation }) => {
  return (
    <View style={styles.container}>
      <Image style={styles.logo} source={require('./path-to-your-logo.png')} />
      <Text style={styles.welcomeText}>Welcome to our restaurant</Text>
      <Text style={styles.descriptionText}>Order food and make reservations with one click</Text>
      <View style={styles.buttonContainer}>
        <Button title="Login" onPress={() => navigation.navigate('LoginScreen')} color={styles.button.backgroundColor} />
        <Button title="Sign Up" onPress={() => navigation.navigate('SignUpScreen')} color={styles.button.backgroundColor} />
      </View>
    </View>
  );
};

export default LandingScreen;
