import React, { useState } from 'react';
import SignUpScreen from './SignUpScreen';
import {
  View,
  Text,
  TextInput,
  Button,
  StyleSheet,
  SafeAreaView,
  ScrollView,
  StatusBar,
} from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';
const LoginScreen = ({ navigation }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = () => {
    try {
      const storedUsername = AsyncStorage.getItem('username');
      const storedPassword = AsyncStorage.getItem('password');
      alert(username + ' ' + password)
      if (username === storedUsername && password === storedPassword) {
        alert('Logged in successfully!');
        navigation.navigate('SuccessScreen');
      } else {
        alert('Invalid username or password!');
      }
    } catch (error) {
      alert(error);
    }
  };

  return (
    <SafeAreaView style={styles.container}>
      <StatusBar barStyle="dark-content" />
      <ScrollView contentContainerStyle={styles.scrollContainer}>
        <View style={styles.inputContainer}>
          <TextInput
            style={styles.input}
            placeholder="Username"
            value={username}
            onChangeText={(text) => setUsername(text)}
          />
        </View>
        <View style={styles.inputContainer}>
          <TextInput
            style={styles.input}
            placeholder="Password"
            secureTextEntry
            value={password}
            onChangeText={(text) => setPassword(text)}
          />
        </View>
        <View style={styles.buttonContainer}>
          <Button title="Login" onPress={handleLogin} />
        </View>
        <Text style={styles.link} onPress={() => navigation.navigate('ForgotPasswordScreen')}>
          Forgot password
        </Text>
        <Text style={styles.link} onPress={() => navigation.navigate('SignUpScreen')}>
          Sign-up
        </Text>
      </ScrollView>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'lightgray',
  },
  scrollContainer: {
    flexGrow: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 16,
  },
  inputContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 16,
    width: '70%',
  },
  input: {
    flex: 1,
    height: 40,
    backgroundColor: 'white',
    borderRadius: 20,
    paddingHorizontal: 16,
  },
  buttonContainer: {
    backgroundColor: '#0099cc',
    width: '70%',
    marginBottom: 16,
    borderRadius: 20,
    overflow: 'hidden', // Ensures child Button respects parent's borderRadius
  },
  link: {
    color: 'black',
    marginTop: 10,
  },
});

export default LoginScreen;
