import React from 'react';
import { View, Text, Image, ScrollView, StyleSheet } from 'react-native';

const HomeScreen = () => {
  return (
    <ScrollView style={styles.container}>
      <View style={styles.section}>
        <Text style={styles.sectionTitle}>Popular Categories</Text>
        <ScrollView horizontal showsHorizontalScrollIndicator={false}>
          <View style={styles.category}><Text>Breakfast</Text></View>
          <View style={styles.category}><Text>Ramen</Text></View>
          <View style={styles.category}><Text>Sandwiches</Text></View>
          <View style={styles.category}><Text>Mexican</Text></View>
        </ScrollView>
      </View>

      <View style={styles.section}>
        <Text style={styles.sectionTitle}>Best Deals</Text>
        <ScrollView horizontal showsHorizontalScrollIndicator={false}>
          <Image source={require('../assets/truck.png')} style={styles.image} />
          <Image source={require('../assets/truck.png')} style={styles.image} />
          <Image source={require('../assets/truck.png')} style={styles.image} />
          <Image source={require('../assets/truck.png')} style={styles.image} />
          {/* Add more images as needed */}
        </ScrollView>
      </View>

      <View style={styles.section}>
        <Text style={styles.sectionTitle}>Most Popular</Text>
        <Image source={require('../assets/truck.png')} style={styles.image} />
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
  },
  section: {
    margin: 10,
  },
  sectionTitle: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  category: {
    marginRight: 10,
  },
  image: {
    width: 200,
    height: 200,
    resizeMode: 'cover',
    marginRight: 10,
  },
  centerImage: {
    alignItems: 'center',
  }
});

export default HomeScreen;
