import React from 'react';
import { View, Text, Image, ScrollView, StyleSheet } from 'react-native';

const HomeScreen = () => {
  return (
    <ScrollView style={styles.container}>
      <View style={styles.section}>
        <Text style={styles.sectionTitle}>Popular Categories</Text>
        <ScrollView horizontal showsHorizontalScrollIndicator={false}>
          <View style={styles.category}>
            <Image source={require('../assets/ramen.jpg')} style={styles.roundImage} resizeMode='contain' />
            <Text>Breakfast</Text>
          </View>
          {/* Add more categories as needed */}
        </ScrollView>
      </View>

      <View style={styles.section}>
        <Text style={styles.sectionTitle}>Best Deals</Text>
        <ScrollView horizontal showsHorizontalScrollIndicator={false}>
          <View style={styles.deal}>
            <Image source={require('../assets/ramen.jpg')} style={styles.blurImage} resizeMode='cover' />
            <Text style={styles.dealText}>Ramen</Text>
          </View>
          {/* Add more deals as needed */}
        </ScrollView>
      </View>

      <View style={styles.section}>
        <Text style={styles.sectionTitle}>Most Popular</Text>
        <View style={styles.popular}>
          <Image source={require('../assets/ramen.jpg')} style={styles.image} resizeMode='cover' />
          <Text style={styles.popularName}>Salad</Text>
          <Text style={styles.popularPrice}>$10</Text>
        </View>
        {/* Add more popular items as needed */}
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
  },
  section: {
    margin: 10,
  },
  sectionTitle: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  category: {
    marginRight: 10,
    alignItems: 'center',
  },
  roundImage: {
    width: 50,
    height: 50,
    borderRadius: 25,
    marginBottom: 5,
  },
  deal: {
    marginRight: 10,
    alignItems: 'center',
    justifyContent: 'center',
  },
  blurImage: {
    width: 150,
    height: 200,
    opacity: 0.5,
  },
  dealText: {
    position: 'absolute',
    color: '#fff',
    fontSize: 18,
    fontWeight: 'bold',
  },
  popular: {
    flexDirection: 'row',
    marginBottom: 10,
  },
  image: {
    width: '90%',
    height: 'auto',
    marginRight: 10,
  },
  popularName: {
    flexShrink: 1,
    flexGrow: 1,
    fontSize: 16,
    fontWeight: 'bold',
  },
  popularPrice: {
    fontSize: 16,
    fontWeight: 'bold',
  },
});

export default HomeScreen;
