import * as React from 'react';
import { View, TextInput, TouchableOpacity, StyleSheet, Text, Image } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { MaterialCommunityIcons } from '@expo/vector-icons';
import { ScrollView } from 'react-native-web';
import { ImageBackground } from 'react-native';
function HomeScreen() {
  return (
    <ScrollView style={styles.container}>
      <View style={styles.header}>
        <View style={styles.search}>
          <MaterialCommunityIcons name="magnify" size={24} color="gray" style={styles.icon} />
          <TextInput
            style={styles.input}
            placeholder="Tìm kiếm..."
          /> 
        </View>        
        <TouchableOpacity style={styles.button}>
          <MaterialCommunityIcons name="format-list-checkbox" size={24} color="#A58EFF" />
        </TouchableOpacity>
      </View> 
      <ScrollView style={styles.body}>
        <View style={styles.Tittle}>
          <Text style={styles.textTittle}>Next to you</Text>
          <View style={styles.ToMap}>
            <Text style={styles.TextToMap}>On the map {'>'}</Text>
          </View>
        </View>
        <ScrollView horizontal={true} style={styles.containerItem} showsHorizontalScrollIndicator={false}>
        <View style={styles.item}>
          <ImageBackground source={require('../assets/icon.png')} style={{width: '232px', height: '228px'}}>  
            <View>
              <View>
                <Text>Until 26.09</Text>
              </View>
              <View>
                <MaterialCommunityIcons name="heart-outline" size={24} color={'white'}/>
              </View>
            </View>
            <View>
              <Image></Image>
              <Text>Anderson G. </Text>
              <Image></Image>
            </View>
          </ImageBackground>
          <View>
            <View>

            </View>
            <View>

            </View>
          </View>
        </View>
        <View style={styles.item}>
          <Text>Item 2</Text>
        </View>
        <View style={styles.item}>
          <Text>Item 3</Text>
        </View>
        {/* Thêm nhiều item khác vào đây */}
      </ScrollView>
      </ScrollView>     
    </ScrollView>
  );
}
const styles = StyleSheet.create({
  ImgCard: {
    
  },
  containerItem: {
    paddingLeft: 10,
    paddingRight: 10,
    paddingTop: 10,
    //backgroundColor: "#FBFBFB",
    backgroundColor: 'red',
  },
  item: {
    width: 264,
    height: 326,
    backgroundColor: 'rgba(255, 255, 255, 0.7)',
    padding: 16,
    marginRight: 10,
    marginBottom: 10,
    marginTop: 5,
    borderRadius: '20px',
  },
  TextToMap: {
    opacity: '0.5',
  },
  ToMap: {
    alignItems: 'flex-end',
    flex: 1,
  },
  Tittle: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  body: {
    marginTop: 30,
  },
  textTittle: {
    flex: 1,
    fontSize: 27,
  },
  search: {
    flexDirection: 'row',
    alignItems: 'center',
    borderRadius: 15,
    width: '70%',
    margin: "auto",
    backgroundColor: 'white',
    borderWidth: 2,
    borderColor: '#F6F6F6',
  },
  header: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  container: {
    paddingLeft: 10,
    paddingRight: 10,
    paddingTop: 10,
    backgroundColor: "#FBFBFB",
    
  },
  icon: {
    marginLeft: 5,
  },
  input: {
    height: 48,
    flex: 1,
    marginLeft: 5,
  },
  button: {
    margin: 'auto'
  },
});
export default HomeScreen;
