import React from 'react';
import { View, Text, Image, ScrollView, StyleSheet, Dimensions  } from 'react-native';
const screenWidth = Dimensions.get('window').width;
const foodCategories = [
  {
    name: 'Category 1',
    image: require('../assets/ramen.jpg'), // Add your image source here
  },
  {
    name: 'Category 2',
    image: require('../assets/ramen.jpg'), // Add your image source here
  },
  {
    name: 'Category 3',
    image: require('../assets/ramen.jpg'), // Add your image source here
  },
  {
    name: 'Category 4',
    image: require('../assets/ramen.jpg'), // Add your image source here
  },
];

const bestDealsSlides = [
  {
    key: 'slide1',
    image: require('../assets/ramen.jpg'),
    foodName: 'Ramen',
  },
  {
    key: 'slide2',
    image: require('../assets/ramen.jpg'),
    foodName: 'Sandwich',
  },
  {
    key: 'slide3',
    image: require('../assets/ramen.jpg'),
    foodName: 'Salad',
  },
  {
    key: 'slide4',
    image: require('../assets/ramen.jpg'),
    foodName: 'Burrito',
  },
];

const popularFoods = [
  {
      name: 'Ramen',
      image: require('../assets/ramen.jpg'), // Add your image source here
      price: '1.00$',
  },
  {
      name: 'Sandwich',
      image: require('../assets/ramen.jpg'), // Add your image source here
      price: '1.00$',
  },
  {
      name: 'Salad',
      price: '1.00$',
      image: require('../assets/ramen.jpg'), // Add your image source here
  },
  {
      name: 'Burrito',
      price: '1.00$',
      image: require('../assets/ramen.jpg'), // Add your image source here
  },
];

const HomeScreen = () => {
  const handleMenuPress = () => {
    navigation.navigate('MenuScreen'); // Navigate to the "Menu" screen
  };
  const renderCategoris = () => {
      return foodCategories.map((category, index) =>(
        <View style={styles.category} key={`category_${index}`}>
            <Image source={category.image} style={styles.categoryImage} />
            <Text style={category.categoryText}> {category.name} </Text>
        </View>
      ));
  }
  const renderBestDealsSlide = ({item}) => {
    return  (
      <View>
        <Image source={item.Image} style={styles.dealImage} ></Image>
        <View style={styles.overlay}>
          <Text style={styles.overlayImage}>{item.foodName}</Text>
        </View>
      </View>
    );
  }

  const renderPopular = () => {
    return popularFoods.map((popular, index) => (
      <View style={styles.popularContainer}>
        <View style={styles.popularItem} key={`popular_${index}`}>
          <Image source={popular.Image} style={styles.popularImage}></Image>
        </View>
        <View style={styles.popularItemText}>
          <Text style={styles.popularText}>{popular.name}</Text>
          <Text style={styles.popularPrice}>{popular.price}</Text>
        </View>        
      </View>
    ));
  }
  return (
    <ScrollView style={styles.container}>
      <View style={styles.section}>
        <Text style={styles.sectionTitle}>Popular Categories</Text>
        <ScrollView horizontal showsHorizontalScrollIndicator={false}>
          <View style={styles.category}>
            <Image source={{uri: 'your_image_url'}} style={styles.roundImage} resizeMode='contain' />
            <Text>Breakfast</Text>
          </View>
          {/* Add more categories as needed */}
        </ScrollView>
      </View>

      <View>
        <Text style={{...styles.sectionTitle, margin: 10,}}>Best Deals</Text>
        <ScrollView horizontal pagingEnabled showsHorizontalScrollIndicator={false}>
          <View style={{...styles.deal, width: screenWidth}}>
            <Image source={require('../assets/ramen.jpg')} style={{...styles.blurImage, width: '100%'}} resizeMode='cover' />
            <Text style={styles.dealText}>Ramen</Text>
          </View>
          <View style={{...styles.deal, width: screenWidth}}>
            <Image source={require('../assets/ramen.jpg')} style={{...styles.blurImage, width: '100%'}} resizeMode='cover' />
            <Text style={styles.dealText}>Ramen</Text>
          </View>
          <View style={{...styles.deal, width: screenWidth}}>
            <Image source={require('../assets/ramen.jpg')} style={{...styles.blurImage, width: '100%'}} resizeMode='cover' />
            <Text style={styles.dealText}>Ramen</Text>
          </View>
          <View style={{...styles.deal, width: screenWidth}}>
            <Image source={require('../assets/ramen.jpg')} style={{...styles.blurImage, width: '100%'}} resizeMode='cover' />
            <Text style={styles.dealText}>Ramen</Text>
          </View>
          <View style={{...styles.deal, width: screenWidth}}>
            <Image source={require('../assets/ramen.jpg')} style={{...styles.blurImage, width: '100%'}} resizeMode='cover' />
            <Text style={styles.dealText}>Ramen</Text>
          </View>
          <View style={{...styles.deal, width: screenWidth}}>
            <Image source={require('../assets/ramen.jpg')} style={{...styles.blurImage, width: '100%'}} resizeMode='cover' />
            <Text style={styles.dealText}>Ramen</Text>
          </View>
          {/* Add more deals as needed */}
        </ScrollView>
      </View>

      <View style={styles.section}>
        <Text style={styles.sectionTitle}>Most Popular</Text>
        <View style={styles.popular}>
          <Image source={require('../assets/ramen.jpg')} style={styles.image} resizeMode='cover' />
          <View style={styles.popularInfo}>
            <Text style={styles.popularName}>Salad</Text>
            <Text style={styles.popularPrice}>$10</Text>
          </View>
        </View>
        {/* Add more popular items as needed */}
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
  },
  section: {
    margin: 10,
  },
  sectionTitle: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  category: {
    marginRight: 10,
    alignItems: 'center',
  },
  roundImage: {
    width: 50,
    height: 50,
    borderRadius: 25,
    marginBottom: 5,
  },
  deal: {
    marginRight: 10,
    alignItems: 'center',
    justifyContent: 'center',
  },
  blurImage: {
    height: 200,
    opacity: 0.8,
  },
  dealText: {
    position: 'absolute',
    color: '#fff',
    fontSize: 18,
    fontWeight: 'bold',
  },
  popular: {
    width: '95%',
    marginBottom: 10,
    alignItems: 'center',
    margin: 'auto',
  },
  image: {
    width: '100%',
    height: 180,
    aspectRatio: 1,
  },
  popularInfo:{
    flexDirection: 'row',
    width: '100%',
   },
   popularName:{
    width: '50%',
     textAlign: 'left',
     fontSize :16, 
     fontWeight:'bold'
   },
   popularPrice:{
     width: '50%',
     textAlign: 'right',
     fontSize :16, 
     fontWeight:'bold'
   }
});

export default HomeScreen;
